and AML_ANALYSISRESULT.RULE_CODE = #{rule}
where AML_DATASOURCE.WORK_DATE like '201908%' or AML_DATASOURCE.WORK_DATE like '201909%' or AML_DATASOURCE.WORK_DATE like '201910%' or AML_DATASOURCE.WORK_DATE like '201911%' or AML_DATASOURCE.WORK_DATE like '201912%' order by aml_datasource.WORK_DATE DESC

